% generate the input cell array and jobs structure to input to SPM to run
% the contrasts
for n = 1:20
    jobfile = {'*/contrast_batch_T_job.m'};
    jobs = repmat(jobfile, 1, 1);
    inputs = cell(2, 1);
    inputs{1, 1} = contrastname{n,:}; % Contrast Manager: Name - cfg_entry
    inputs{2, 1} = contrasts(n,:); % Contrast Manager: Weights matrix - cfg_entry
    spm('defaults', 'FMRI');
    spm_jobman('run', jobs, inputs{:});
    clear inputs
end
